# -*- coding:utf-8 -*-
class MagicIndex:
    # 和最初的思路一致，采用二分法
    def bSearch(self, array, left, right):
        if (left > right or left < 0 or right >= len(array)):
            return False
        mid = (left + right) // 2
        if mid == array[mid]:
            return True
        elif mid > array[mid]:  # 往右边找
            left = mid + 1
            return self.bSearch(array, left, right)
        else:  # 往左边找
            right = mid - 1
            return self.bSearch(array, left, right)     #这里要记得写上return，不然无法将True返回

    def findMagicIndex(self, A, n):
        return self.bSearch(A, 0, len(A) - 1)

